 */
class KunstmaanAdminExtensionTest extends PHPUnit_Framework_TestCase
{
    /**
     * @var KunstmaanAdminExtension
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    public function setUp()
    {
        $this->object = new KunstmaanAdminExtension();
        $this->object->load([], new ContainerBuilder());
    }

    public function testGetSet()
    {
        $this->assertEquals('http://bundles.kunstmaan.be/schema/dic/admin', $this->object->getNamespace());
