        $this->assertEquals(10, $rule->getSeconds());
        $this->assertEquals('AfterXSecondsRule', $rule->getJsObjectClass());
        $this->assertEquals('/bundles/kunstmaanleadgeneration/js/rule/AfterXSecondsRule.js', $rule->getJsFilePath());
